import alt from 'alt-instance'

class GroceryListActions {
  fetchLists() {
    return (dispatch) => dispatch();
  }

  fetchSuccess(lists) {
    return (dispatch) => dispatch(lists);
  }

  fetchFailure(err) {
    return (dispatch) => dispatch(err);
  }

  addList(list) {
    return (dispatch) => dispatch(list);
  }

  removeList(listId) {
    return (dispatch) => dispatch(listId);
  }

  addItem(listId, item) {
    return (dispatch) => dispatch({ listId, item });
  }

  removeItem(listId, itemId) {
    return (dispatch) => dispatch({ listId, itemId });
  }

  toggleItem(listId, itemId) {
    return (dispatch) => dispatch({ listId, itemId });
  }
}

export default alt.createActions(GroceryListActions);

