import alt from 'client/app/alt';

class MealsActionCreators {
  fetchMeals() {return {};}
  fetchMealsSuccess(mealsList) {return mealsList;}
  fetchMealsFailure(error) {return error;}

  addMeal(date, name) {return {date,name};}
  addMealSuccess(mealsList) {return mealsList;}
  addMealFailure(error) {return error;}

  /**
   *
   * @param date Date object corresponding to new date
   */
  changeDate(date) {return date;}
}

export default alt.createActions(MealsActionCreators);
