import assign from 'lodash/assign';
import alt from 'alt-instance';

class PlannerActions {
  changeDate = (date) => date;
  addRecipe = (recipe, date, meal) => ({recipe, date, meal});
  generateGroceries = (entry) => entry;
  showModal = ({date, meal, recipe}) => ({date, meal, recipe});
}

export default assign(
  alt.generateActions('fetchPlanner', 'closeModal'),
  alt.createActions(PlannerActions),
);
