import assign from 'lodash/assign';
import alt from 'alt-instance';

class RecipeActions {
  loadRecipe(recipe) {
    return recipe;
  }

  updateRecipe(key, value) {
    return {key, value};
  }

  submitSuccess(response) {
    return response;
  }

  submitFailure(err) {
    return err;
  }

  updateIngredient(ingredientId, key, value) {
    return {ingredientId, key, value};
  }

  removeIngredient(ingredientId) {
    return ingredientId;
  }
}

export default assign(
  {},
  alt.generateActions('submitRecipe', 'addIngredient'),
  alt.createActions(RecipeActions),
);
