import React, {PropTypes} from 'react';
import {FormGroup, InputGroup, FormControl, Button, Glyphicon} from 'react-bootstrap';
import IngredientsAutocomplete from 'client/app/components/shared/IngredientsAutocomplete';

import './AddMeal.scss';

const AddMeal = ({value, placeholder, onChange, onSubmit}) => (
  <FormGroup className="s__AddMeal">
    <InputGroup>
      <IngredientsAutocomplete onIngredientSelected={onChange}/>
      <InputGroup.Button>
        <Button onClick={onSubmit} aria-label="Add"><Glyphicon glyph="plus" /></Button>
      </InputGroup.Button>
    </InputGroup>
  </FormGroup>
);

AddMeal.propTypes = {
  value: PropTypes.string,
  placeholder: PropTypes.string,
  onChange: PropTypes.func,
  onSubmit: PropTypes.func,
};

AddMeal.defaultProps = {
  onChange: () => {},
  onSubmit: () => {},
};

export default AddMeal;
