import React from 'react'

import ListGroup from 'react-bootstrap/lib/ListGroup';
import ListGroupItem from 'react-bootstrap/lib/ListGroupItem';
import AddInputContainer from 'client/app/components/shared/form/AddInputContainer';

const FoodLog = ({meals, onMealAdded}) => {
  return (
    <div>
      <h2>Food Log</h2>
      <ListGroup>
        {meals.map(meal => (<ListGroupItem key={meal._id}>{meal.name}</ListGroupItem>))}
      </ListGroup>
      <AddInputContainer
        placeholder="Add meal"
        onAdd={value => onMealAdded(value)}
      />
    </div>
  );
};

export default FoodLog;