import React from 'react';
import {If, Then} from 'react-if';
import {Grid, Row, Col} from 'react-bootstrap';

import GroceriesSidebar from './aside/GroceriesSidebar';
import GroceryListContainer from './list/GroceryListContainer';

import './groceries.scss';

const Groceries = ({lists, activeList}) => (
  <Grid>
    <Row>
      <Col xs={12} md={3}>
        <GroceriesSidebar lists={lists} />
      </Col>
      <Col xs={12} md={9}>
        <If condition={activeList != null}>
          <Then>{() =>
            <GroceryListContainer list={activeList} />}
          </Then>
        </If>
      </Col>
    </Row>
  </Grid>
);

Groceries.propTypes = {
  lists: React.PropTypes.array.isRequired,
  activeList: React.PropTypes.object,
};

export default Groceries;
