import React from 'react'
import { If, Then } from 'react-if'
import { Grid, Row, Col } from 'react-bootstrap'
import GroceryListActions from 'actions/GroceryListActions'

import ListNav from './nav/GroceryListNav'
import ListContainer from './list/GroceryListContainer'

function addList(lists) {
  GroceryListActions.addList({
    _id : lists.listName.value,
    title: lists.listName.value,
    items : []
  }),
    lists.listName.value = ""
}

const Groceries = ({ lists, activeList }) => (
    <Grid>
      <Row>
        <Col xs={12} md={3}>
          <input type="text" ref={(ref) => lists.listName = ref } placeholder="List name.."/>
          <button className="add-list" onClick={() => addList(lists)}>+</button>
          <ListNav lists={lists} />
        </Col>
        <Col xs={12} md={9}>
          <If condition={activeList != null}>
            <Then>{() =>
                <ListContainer list={activeList} />}
            </Then>
          </If>
        </Col>
      </Row>
    </Grid>
);

Groceries.propTypes = {
  lists:      React.PropTypes.array.isRequired,
  activeList: React.PropTypes.object
};

export default Groceries;
