import React from 'react'
import { If, Then } from 'react-if'
import { Grid, Row, Col } from 'react-bootstrap'
import GroceryListActions from 'actions/GroceryListActions'

import ListNav from './nav/GroceryListNav'
import ListContainer from './list/GroceryListContainer'

const Groceries = ({ lists, activeList, onItemSubmit, onItemChange, inputText }) => (
    <Grid>
      <Row>
        <Col xs={12} md={3}>
          <input type="text" onChange={(event) => inputText = onItemChange(event)} placeholder="List name.."/>
          <button className="add-list" onClick={() => onItemSubmit(inputText)}>+</button>
          <ListNav lists={lists} />
        </Col>
        <Col xs={12} md={9}>
          <If condition={activeList != null}>
            <Then>{() =>
                <ListContainer list={activeList} />}
            </Then>
          </If>
        </Col>
      </Row>
    </Grid>
);

Groceries.propTypes = {
  lists:      React.PropTypes.array.isRequired,
  activeList: React.PropTypes.object,
  onItemSubmit: React.PropTypes.func.isRequired,
  onItemChange: React.PropTypes.func.isRequired

};

export default Groceries;
