import React from 'react';
import {If, Then} from 'react-if';
import {Grid, Row, Col} from 'react-bootstrap';

import ListNav from './nav/GroceryListNav';
import ListContainer from './list/GroceryListContainer';

const Groceries = ({lists, activeList, onListSubmit, onTitleChange, onListDelete}) => (
  <Grid>
    <Row>
      <Col xs={12} md={3}>
        <input type="text" onChange={(event) => onTitleChange(event.target.value)} placeholder="List name.." />
        <button className="add-list" onClick={onListSubmit}>+</button>
        <ListNav lists={lists} onDelete={onListDelete} />
      </Col>
      <Col xs={12} md={9}>
        <If condition={activeList != null}>
          <Then>{() =>
            <ListContainer list={activeList} />}
          </Then>
        </If>
      </Col>
    </Row>
  </Grid>
);

Groceries.propTypes = {
  lists: React.PropTypes.array.isRequired,
  activeList: React.PropTypes.object,
  onListSubmit: React.PropTypes.func,
  onTitleChange: React.PropTypes.func,
  onListDelete: React.PropTypes.func,
};

Groceries.defaultProps = {
  onListSubmit: () => {},
  onTitleChange: () => {},
};

export default Groceries;
