import React from 'react';
import GroceryListActions from 'actions/GroceryListActions';

import AddInputC from 'client/app/components/shared/form/AddInputC';
import GroceryListNav from './GroceryListNav';

import './groceries-sidebar.scss';

export default class GroceryListSidebar extends React.Component {
  constructor(...args) {
    super(...args);
  }

  render() {
    return (
      <div>
        <AddInputC
          placeholder="Add list..."
          onAdd={value => GroceryListActions.createList({title: value})}
        />
        <GroceryListNav
          onListDelete={listId => GroceryListActions.removeList(listId)}
          {...this.props}
        />
      </div>
    );
  }
}