import React, {PropTypes} from 'react';
import {Nav} from 'react-bootstrap';
import GroceryListSchema from 'schemas/GroceryListSchema';

import GroceryListNavItem from './GroceryListNavItem';

const GroceryListNav = ({lists, onSelect, onListDelete, onListSubmit, onList}) => (
  <nav className="grocery-list-nav">
    <Nav bsStyle="pills" stacked onSelect={onSelect}>
      {lists.map(list => (
        <GroceryListNavItem list={list} key={list._id} onDelete={() => onDelete(list._id)} />
      ))}
    </Nav>
  </nav>
);

GroceryListNav.propTypes = {
  lists: PropTypes.arrayOf(GroceryListSchema.only('_id')).isRequired,
  onSelect: PropTypes.func,
  onDelete: PropTypes.func,
};

GroceryListNav.defaultProps = {
  onSelect: () => {},
};

export default GroceryListNav;
