import React, {PropTypes} from 'react';
import {Link} from 'react-router';
import DeleteButton from 'components/shared/DeleteButton';
import GroceryListSchema from 'schemas/GroceryListSchema';

const GroceryListNavItem = ({list, onDelete}) => {
  const handleDelete = (event) => {
    event.stopPropagation();
    onDelete(event);
  };

  return (
    <li role="presentation">
      <Link to={`groceries/${list._id}`} activeClassName="active">
        {list.title}
        <DeleteButton onClick={handleDelete}
                      className="pull-right" />
      </Link>
    </li>
  );
}

GroceryListNavItem.propTypes = {
  list: GroceryListSchema.only('_id', 'title').isRequired,
  onDelete: PropTypes.func,
};

GroceryListNavItem.defaultProps = {
  onDelete: () => {},
};

export default GroceryListNavItem;
