import React  from 'react'
import ListSchema from 'schemas/GroceryListSchema'

import Item from './GroceryListItem'

import './grocery_list.scss'

const List = ({list, onItemToggle, onItemChange, onItemSubmit, onItemDelete}) => (
  <div>
    <div className="list-container">
      <h3>{list.title}</h3>
    </div>
    <input type="text" onChange={onItemChange} placeholder="Add item.." />
    <button className="add-item" onClick={onItemSubmit}>+</button>
    <ul className="grocery-list">
      {list.items.map(item => (
        <Item listId={list._id}
              item={item}
              onToggle={() => onItemToggle(item._id)}
              onDelete={() => onItemDelete(item._id)}
              key={item._id} />
      ))}
    </ul>
  </div>
);

List.propTypes = {
  list: ListSchema.isRequired,
  onItemToggle: React.PropTypes.func,
  onItemChange: React.PropTypes.func,
  onItemSubmit: React.PropTypes.func,
  onItemDelete: React.PropTypes.func,
};

List.defaultProps = {
  onItemChange: () => {},
  onItemSubmit: () => {},
};

export default List;
