import React from 'react';
import GroceryListSchema from 'schemas/GroceryListSchema';

import GroceryListItem from './GroceryListItem';
import AddItemForm from './AddItemForm';

import './grocery_list.scss';

const GroceryList = ({list, onItemToggle, onItemDelete}) => (
  <div className="list-container">
    <h3>{list.title}</h3>
    <AddItemForm list={list} />
    <ul className="grocery-list">
      {list.items.map(item => (
        <GroceryListItem listId={list._id}
                         item={item}
                         onToggle={() => onItemToggle(item._id)}
                         onDelete={() => onItemDelete(item._id)}
                         key={item._id} />
      ))}
    </ul>
  </div>
);

GroceryList.propTypes = {
  list: GroceryListSchema.isRequired,
  onItemToggle: React.PropTypes.func,
  onItemDelete: React.PropTypes.func,
};

export default GroceryList;
