import React, {PropTypes} from 'react'
import {FormGroup, Checkbox} from 'react-bootstrap'

import GroceryListItemSchema from 'schemas/GroceryListItemSchema'
import DeleteButton from 'components/shared/DeleteButton';

const GroceryListItem = ({listId, item, onToggle, onDelete}) => (
  <li>
    <FormGroup>
      <Checkbox checked={item.completed}
                onChange={onToggle}>
        {item.name}
        <DeleteButton onClick={onDelete} className="pull-right" />
      </Checkbox>
    </FormGroup>
  </li>
);

GroceryListItem.propTypes = {
  item: GroceryListItemSchema.only('name', 'completed').isRequired,
  onToggle: PropTypes.func,
  onDelete: PropTypes.func,
};

GroceryListItem.propTypes = {
  onToggle: () => {},
  onDelete: () => {},
};

export default GroceryListItem;
