import React, { PropTypes } from 'react'
import { FormGroup, Checkbox } from 'react-bootstrap'

import ItemSchema from 'schemas/GroceryListItemSchema'
import DeleteButton from 'components/shared/DeleteButton';

const Item = ({ listId, item, onToggle, onDelete}) => (
  <li>
    <FormGroup>
      <Checkbox checked={item.completed}
                onChange={onToggle}>
        {item.name}
      </Checkbox>
      <DeleteButton onClick={onDelete}/>
    </FormGroup>
  </li>
);

Item.propTypes = {
  item:     ItemSchema.only('name', 'completed').isRequired,
  onToggle: PropTypes.func,
  onDelete: PropTypes.func,
};

Item.propTypes = {
  onToggle: () => {},
  onDelete: () => {},
};

export default Item;
