import React, { PropTypes } from 'react'
import { FormGroup, Checkbox } from 'react-bootstrap'

import ItemSchema from 'schemas/GroceryListItemSchema'
import GroceryListActions from 'actions/GroceryListActions'
import DeleteButton from 'sources/Svg'



const Item = ({ listId, item, onToggle}) => (
  <li>
    <FormGroup>
      <Checkbox checked={item.completed}
                onChange={onToggle}>
        {item.name}
      </Checkbox>
      <DeleteButton onClick={() => (GroceryListActions.removeItem(listId, item._id))}/>
    </FormGroup>
  </li>
);

Item.propTypes = {
  item:     ItemSchema.only('name', 'completed').isRequired,
  onToggle: PropTypes.func.isRequired,
};

export default Item;
