import React from 'react'
import { LinkContainer } from 'react-router-bootstrap'
import { NavItem, Glyphicon } from 'react-bootstrap'

const { string, object, oneOfType } = React.PropTypes;

export default class IconLink extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    let { to, glyph, text, ...props } = this.props;
    let desc = text ? <span className="navbar-icon-desc">{text}</span> : '';
    return (
        <LinkContainer to={to}>
          <NavItem {...props} className="navbar-icon">
            <Glyphicon glyph={glyph} aria-hidden={true} />
            {desc}
          </NavItem>
        </LinkContainer>
    );
  }
}

IconLink.propTypes = {
  to: oneOfType([ string, object ]).isRequired,
  glyph: string.isRequired,
  className: string,
  text: string
};
