import React from 'react'
import { NavItem, Glyphicon } from 'react-bootstrap'

export default class IconNavItem extends React.Component {
  render() {
    let {glyph, text, ...props } = this.props;
    let desc = text ? <span className="navbar-icon-desc">{text}</span> : '';
    let style = this.props.className ? `${this.props.className} navbar-icon` : 'navbar-icon';
    return (
      <NavItem {...props} className={style}>
        <Glyphicon glyph={glyph} aria-hidden={true} />
        {desc}
      </NavItem>
    );
  }
}

IconNavItem.propTypes = {
  glyph: React.PropTypes.string.isRequired,
  className: React.PropTypes.string,
  text: React.PropTypes.string
};
