import React from 'react'

import { FormGroup, FormControl, InputGroup, Button, Glyphicon } from 'react-bootstrap'

export default class Omnibox extends React.Component {
  constructor(props) {
    super(props);
    this.state = { searchTerm: '' };
    this._onChange = this._onChange.bind(this);
    this._onSearch = this._onSearch.bind(this);
  }

  _onChange(e) {
    this.setState({ searchTerm: e.target.value });
  }

  _onSearch(e) {
    e.preventDefault();
    this.props.onSearch(this.state.searchTerm);
  }

  render() {
    return (
        <form>
          <FormGroup>
            <InputGroup>
              <FormControl type="text" placeholder="Search" onChange={this._onChange} />
              <InputGroup.Button>
                <Button type="submit" onClick={this._onSearch}>
                  <Glyphicon glyph="search" aria-label="Search" />
                </Button>
              </InputGroup.Button>
            </InputGroup>
          </FormGroup>
        </form>
    );
  }
}

Omnibox.propTypes = {
  onSearch: React.PropTypes.func.isRequired
};
