import React from 'react'
import { LinkContainer } from 'react-router-bootstrap'
import { NavDropdown, MenuItem, Glyphicon } from 'react-bootstrap'

export default class SettingsDropdown extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
        <NavDropdown {...this.props}
                     title={<Glyphicon glyph="cog" />}
                     className="navbar-settings-dropdown"
                     id="navbar-settings-dropdown"
                     aria-hidden={true}>
          <LinkContainer to="/settings">
            <MenuItem>
              <Glyphicon glyph="cog" aria-hidden={true} />
              <span>Settings</span>
            </MenuItem>
          </LinkContainer>
          <LinkContainer to="/profile">
            <MenuItem>
              <Glyphicon glyph="user" aria-hidden={true} />
              <span>Profile</span>
            </MenuItem>
          </LinkContainer>
          <MenuItem divider />
          <LinkContainer to="/notifications">
            <MenuItem>
              <Glyphicon glyph="bell" aria-hidden={true} />
              <span>Notifications</span>
            </MenuItem>
          </LinkContainer>
        </NavDropdown>
    );
  }
}