import React from 'react';
import PlannerEntrySchema from 'schemas/PlannerEntrySchema';
import PlannerActions from 'actions/PlannerActions';
import CartButton from 'components/shared/CartButton';

const PlannerEntry = ({entry}) => (
  <div>
    <p>{entry.name ? entry.name : entry.recipe.title}</p>
    <CartButton onClick={() => {PlannerActions.generateGroceries(entry)}} className="cart-button"/>
  </div>
);

PlannerEntry.propTypes = {
  entry: PlannerEntrySchema.isRequired
};

export default PlannerEntry;
