import React, {PropTypes} from 'react';
import {Button} from 'react-bootstrap';
import Recipe from 'schemas/RecipeSchema';

import Card from 'components/shared/card/Card';

const RecipeResults = ({recipes, onRecipeSet}) => (
  <div>
    {recipes.map(recipe => (
      <Card key={recipe._id}>
        <Card.Title title={recipe.title} />
        <Card.Actions>
          <Button onClick={() => onRecipeSet(recipe)}>ADD</Button>
          <a href={`/recipes/${recipe._id}`} target="_blank">SHOW RECIPE</a>
        </Card.Actions>
      </Card>
      )
    )}
  </div>
);

RecipeResults.propTypes = {
  recipes: PropTypes.arrayOf(Recipe).isRequired,
  onRecipeAdd: PropTypes.func,
  onRecipeSet: PropTypes.func,
};

export default RecipeResults;
