import React, {PropTypes} from 'react';
import {Grid, Row} from 'react-bootstrap';
import {Link} from 'react-router';
import RecipeSchema from 'schemas/RecipeSchema';

import Cards from 'components/shared/card/Cards';
import ActionButton from 'components/shared/ActionButton';
import RecipeCard from './RecipeCard';

const RecipesView = ({recipes}) => (
  <Grid>
    <Link to="/recipes/new">
      <ActionButton icon="plus" />
    </Link>
    <Row>
      <Cards>
        {recipes.map(recipe => <RecipeCard recipe={recipe} key={recipe._id} />)}
      </Cards>
    </Row>
  </Grid>
);

RecipesView.propTypes = {
  recipes: PropTypes.arrayOf(RecipeSchema.only('_id')).isRequired
};

export default RecipesView;
