import React, {PropTypes} from 'react';
import {FormControl, Button} from 'react-bootstrap';
import {IngredientTypes} from 'schemas/IngredientSchema';

const IngredientFormGroup = ({ingredient, onChange, onDelete, removable}) => (
  <div className="form-inline">
    <FormControl name="name"
                 value={ingredient.name || ''}
                 placeholder="Name"
                 onChange={e => onChange('name', e.target.value)} />
    <FormControl name="qty"
                 value={ingredient.qty || ''}
                 placeholder="Quantity"
                 onChange={e => onChange('qty', e.target.value)} />
    <FormControl name="unit"
                 value={ingredient.unit || ''}
                 placeholder="Measure"
                 onChange={e => onChange('unit', e.target.value)} />
    <FormControl name="note"
                 value={ingredient.note || ''}
                 placeholder="PrepNotes"
                 onChange={e => onChange('note', e.target.value)} />
    {removable ? <Button onClick={onDelete}>-</Button> : ''}
  </div>
);

IngredientFormGroup.propTypes = {
  ingredient: IngredientTypes,
  onChange: PropTypes.func,
  onDelete: PropTypes.func,
  removable: PropTypes.bool,
};

IngredientFormGroup.defaultProps = {
  ingredient: {},
  onChange: () => {},
  removable: true,
};

export default IngredientFormGroup;
