
const mealList = [
  {_id: '1', name: 'meal 1'},
  {_id: '2', name: 'meal 2'},
  {_id: '3', name: 'meal 3'},
];
var nextId = 4;

const MealsSource = {
  fetch: () => Promise.resolve(mealList),
  add: (name) => {
    mealList.push({_id: nextId, name});
    nextId += 1;
    return Promise.resolve(mealList);
  }
};

export default MealsSource;
