import alt from 'client/app/alt';

import MealsCrudActions from 'client/app/actions/MealsCrudActions';

class MealsStore {
  constructor() {
    this.date = null;
    this.meals = [];

    this.bindListeners({
      refreshMeals: [MealsCrudActions.FETCH_MEALS_SUCCESS, MealsCrudActions.ADD_MEAL_SUCCESS]
    })
  }

  refreshMeals(mealsList) {
    this.setState({meals: mealsList});
  }
}

export default alt.createStore(MealsStore, 'MealsStore');