'use strict';

var mongoose = require('bluebird').promisifyAll(require('mongoose')),
  Schema = mongoose.Schema;

var MealSchema = new Schema({
  name: {
    type: String,
    required: true,
  },
  date: {
    type: String,
    match: /^\d{8}$/,
    required: true,
  }
});

export default mongoose.model('Meal', MealSchema);
