/**
 * Populate DB with sample data on server start
 * to disable, edit config/environment/index.js, and set `seedDB: false`
 */

'use strict';
import Recipe from '../api/recipe/recipe.model';
import User from '../api/user/user.model';
import GroceryList from '../api/grocery_list/grocery_list.model';

User.find({}).removeAsync()
  .then(() => {
    User.createAsync({
        provider: 'local',
        name: 'test',
        email: 'test@example.com',
        password: 'test',
      }, {
        provider: 'local',
        role: 'admin',
        name: 'admin',
        email: 'admin@example.com',
        password: 'admin',
      })
      .then(() => {
        console.log('finished populating users');
      });
  });

Recipe.find({}).removeAsync()
  .then(() => {
    Recipe.create({
      title: 'Spaghetti Bolognese',
      author: 'test',
    }, {
      title: 'Spaghetti Carbonara',
      author: 'test',
    }, {
      title: 'Spaghetti and Meatballs',
      author: 'test',
    }, {
      title: 'A Very Very Long Recipe Title Spanning Multiple Lines',
      author: 'test',
    }, {
      title: 'Garlic Spaghetti',
      author: 'test',
    }, {
      title: 'Parsley Pesto Spaghetti',
    });
  });

GroceryList.find({}).removeAsync()
  .then(() =>
    GroceryList.create({
      title: 'A list',
      items: [{name: 'Item 1'}, {name: 'Item 2'}],
    }, {
      title: 'Another list',
      items: [{name: 'a'}, {name: 'b'}, {name: 'c'}],
    })
  )
  .then(() => console.log('finished populating grocery lists'));
