'use strict';

import logger from 'winston'
import Recipe from '../api/recipe/recipe.model';
import User from '../api/user/user.model';

User.find({}).removeAsync()
  .then(() => {
    User.createAsync({
      provider: 'local',
      name: 'test',
      email: 'test@example.com',
      password: 'test',
    }, {
      provider: 'local',
      role: 'admin',
      name: 'admin',
      email: 'admin@example.com',
      password: 'admin',
    })
      .then(() => {
        logger.info('finished populating users');
      });
  });

Recipe.find({}).removeAsync()
  .then(() => {
    Recipe.create({
      title: 'Spaghetti Bolognese',
      author: 'test',
    }, {
      title: 'Spaghetti Carbonara',
      author: 'test',
    }, {
      title: 'Spaghetti and Meatballs',
      author: 'test',
    }, {
      title: 'A Very Very Long Recipe Title Spanning Multiple Lines',
      author: 'test',
    }, {
      title: 'Garlic Spaghetti',
      author: 'test',
    }, {
      title: 'Parsley Pesto Spaghetti',
    });
  });
