'use strict';

const proxyquire = require('proxyquire').noPreserveCache();

const groceryListCtrlStub = {
  index: 'groceryListCtrl.index',
  show: 'groceryListCtrl.show',
  create: 'groceryListCtrl.create',
  upsert: 'groceryListCtrl.upsert',
  patch: 'groceryListCtrl.patch',
  destroy: 'groceryListCtrl.destroy',
};

const authServiceStub = {
  isAuthenticated() {
    return 'authService.isAuthenticated';
  },
  hasRole(role) {
    return `authService.hasRole.${role}`;
  },
};

const routerStub = {
  get: sinon.spy(),
  put: sinon.spy(),
  patch: sinon.spy(),
  post: sinon.spy(),
  delete: sinon.spy(),
};

// require the index with our stubbed out modules
const groceryListIndex = proxyquire('server/api/grocery_list/index.js', {
  express: {
    Router: () => routerStub,
  },
  './grocery_list.controller': {
    default: () => groceryListCtrlStub,
  },
  '../../auth/auth.service': authServiceStub,
}).default;

describe('GroceryList API Router:', () => {
  it('should return an express router instance', () => {
    groceryListIndex.should.equal(routerStub);
  });

  describe('GET /api/grocery_lists', () => {
    it('should route to groceryList.controller.index', () => {
      routerStub.get
        .withArgs('/', 'authService.isAuthenticated', 'groceryListCtrl.index')
        .should.have.been.calledOnce;
    });
  });

  describe('GET /api/grocery_lists/:id', () => {
    it('should route to groceryList.controller.show', () => {
      routerStub.get
        .withArgs('/:id', 'authService.isAuthenticated', 'groceryListCtrl.show')
        .should.have.been.calledOnce;
    });
  });

  describe('POST /api/grocery_lists', () => {
    it('should route to groceryList.controller.create', () => {
      routerStub.post
        .withArgs('/', 'authService.isAuthenticated', 'groceryListCtrl.create')
        .should.have.been.calledOnce;
    });
  });

  describe('PUT /api/grocery_lists/:id', () => {
    it('should route to groceryList.controller.upsert', () => {
      routerStub.put
        .withArgs('/:id', 'authService.isAuthenticated', 'groceryListCtrl.upsert')
        .should.have.been.calledOnce;
    });
  });

  describe('PATCH /api/grocery_lists/:id', () => {
    it('should route to groceryList.controller.patch', () => {
      routerStub.patch
        .withArgs('/:id', 'authService.isAuthenticated', 'groceryListCtrl.patch')
        .should.have.been.calledOnce;
    });
  });

  describe('DELETE /api/grocery_lists/:id', () => {
    it('should route to groceryList.controller.destroy', () => {
      routerStub.delete
        .withArgs('/:id', 'authService.isAuthenticated', 'groceryListCtrl.destroy')
        .should.have.been.calledOnce;
    });
  });
});
