'use strict';

var proxyquire = require('proxyquire').noPreserveCache();

var groceryListCtrlStub = {
  index: 'groceryListCtrl.index',
  show: 'groceryListCtrl.show',
  create: 'groceryListCtrl.create',
  upsert: 'groceryListCtrl.upsert',
  patch: 'groceryListCtrl.patch',
  destroy: 'groceryListCtrl.destroy'
};

var routerStub = {
  get: sinon.spy(),
  put: sinon.spy(),
  patch: sinon.spy(),
  post: sinon.spy(),
  delete: sinon.spy()
};

// require the index with our stubbed out modules
var groceryListIndex = proxyquire('server/api/grocery_list/index.js', {
  express: {
    Router() {
      return routerStub;
    }
  },
  './grocery_list.controller': groceryListCtrlStub
}).default;

describe('GroceryList API Router:', function() {
  it('should return an express router instance', function() {
    groceryListIndex.should.equal(routerStub);
  });

  describe('GET /api/grocery_lists', function() {
    it('should route to groceryList.controller.index', function() {
      routerStub.get
        .withArgs('/', 'groceryListCtrl.index')
        .should.have.been.calledOnce;
    });
  });

  describe('GET /api/grocery_lists/:id', function() {
    it('should route to groceryList.controller.show', function() {
      routerStub.get
        .withArgs('/:id', 'groceryListCtrl.show')
        .should.have.been.calledOnce;
    });
  });

  describe('POST /api/grocery_lists', function() {
    it('should route to groceryList.controller.create', function() {
      routerStub.post
        .withArgs('/', 'groceryListCtrl.create')
        .should.have.been.calledOnce;
    });
  });

  describe('PUT /api/grocery_lists/:id', function() {
    it('should route to groceryList.controller.upsert', function() {
      routerStub.put
        .withArgs('/:id', 'groceryListCtrl.upsert')
        .should.have.been.calledOnce;
    });
  });

  describe('PATCH /api/grocery_lists/:id', function() {
    it('should route to groceryList.controller.patch', function() {
      routerStub.patch
        .withArgs('/:id', 'groceryListCtrl.patch')
        .should.have.been.calledOnce;
    });
  });

  describe('DELETE /api/grocery_lists/:id', function() {
    it('should route to groceryList.controller.destroy', function() {
      routerStub.delete
        .withArgs('/:id', 'groceryListCtrl.destroy')
        .should.have.been.calledOnce;
    });
  });
});
